      <ListItemIcon>
        <VerifiedUserIcon />
      </ListItemIcon>
      <ListItemText primary="Verify Docs" />
    </ListItem>

    <ListItem component='a' href={"/#/dataentry"} button>
      <ListItemIcon>
        <EditIcon />
      </ListItemIcon>
      <ListItemText primary="Data Entry" />
    </ListItem>   

  </div>
);

