import 'date-fns';
import React from 'react';
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';


class MaterialUIPickers extends React.Component {

  constructor(props) {
      super(props);
      this.state = {

      };
      // this.loadProductList = this.loadProductList.bind(this);
      // this.getReportDetails = this.getReportDetails.bind(this);
      // this.convertBool = this.convertBool.bind(this);
      // this.onChange = this.onChange.bind(this);
      // this.myCallback = this.myCallback.bind(this);
 //     this.handleKeypress = this.handleKeypress.bind(this);

 var startd = new Date(); 
 startd.setHours(0,0,0,0);

 var endd = new Date(); 
 endd.setHours(23,59,59,59);

 const [selectedStartDate, setSelectedStartDate] = React.useState(startd);
 const [selectedEndDate, setSelectedEndDate] = React.useState(endd);


  }

  render() {

    const dateParam = {
        myCallback: this.myCallback,
        startDate: '',
        endDate: ''

    }


      var requiredDate ='';


      // The first commit of Material-UI

      const handleStartDateChange = date => {
        setSelectedStartDate(date);
        this.props.callbackFromParent.myCallback(date,"START_DATE");
      };
      const handleEndDateChange = date => {
        console.log(date,"SELECTED_DATE")
        setSelectedEndDate(date);
        this.props.callbackFromParent.myCallback(date,"END_DATE");
      };


      // var date = new Date(); 
      // date.getMonth() - 6; 


      var dateObj = new Date(selectedStartDate);



      var requiredDate = dateObj.setMonth(dateObj.getMonth() + 6);


      var currDate = new Date();
      var back18Month= currDate.setMonth(currDate.getMonth() - 18);
      console.log(new Date(back18Month),"back18Month");

      if(selectedStartDate <  back18Month){
        selectedStartDate = back18Month;
      }

      if(selectedEndDate <  selectedStartDate){
        selectedEndDate = requiredDate;
      }

      // if(requiredDate > new Date() ) {
      //   requiredDate = new Date();
      // }
    // var requiredDate = selectedStartDate.setMonth(selectedStartDate.getMonth() + 6)

    return (
        <MuiPickersUtilsProvider utils={DateFnsUtils}>
          <Grid container justify="space-around">

            <KeyboardDatePicker
              margin="normal"
              //readOnly="true"
              // disabled="true"
              disableFuture="true"
              allowKeyboardControl="true"
              minDate={back18Month}
              minDateMessage="Only 18 months back report can be fatch."
              id="date-picker-dialog"
              label="Start Date"
              format="dd/MM/yyyy"
              value={selectedStartDate}
              onChange={handleStartDateChange}
              KeyboardButtonProps={{
                'aria-label': 'change date',
              }}
            />
            <KeyboardDatePicker

              disableFuture="true"
              maxDateMessage="Max allowed date range is 6 months."
              minDateMessage="End date can't be less than start date."
              minDate={selectedStartDate}
              maxDate={requiredDate}
              margin="normal"
              id="date-picker-dialog"
              label="End Date"
              format="dd/MM/yyyy"
              value={selectedEndDate}
              onChange={handleEndDateChange}
              KeyboardButtonProps={{
                'aria-label': 'change date',
              }}
            />

          </Grid>
        </MuiPickersUtilsProvider>
      );

}

}

export default MaterialUIPickers;




